import React, { useState } from "react";
import Counter from "./counter";

function Counters() {
  let [counters, setCounter] = useState([
    { id: 1, value: 4 },
    { id: 2, value: 0 },
    { id: 3, value: 0 },
    { id: 4, value: 0 },
  ]);

  let handleDelete = (counterId) => {
    const counters = counters.filter(c.id !== counterId);
    setCounter({ counters });
  };

  return (
    <div>
      {counters.map((counter) => (
        <Counter
          key={counter.id}
          onDelete={handleDelete}
          value={counter.value}
          id={counter.id}
        >
          <h2>{counter.id}</h2>
        </Counter>
      ))}
    </div>
  );
}

export default Counters;
