import React, { useState } from "react";

function Counter(props) {
  console.log(props);
  const [value, setCount] = useState(props.counter.value);

  let classes = getBadgeClasses();

  let handleIncrement = () => {
    console.log("Increase", value);
    setCount(value + 1);
  };

  return (
    <div>
      <span className={classes}>{formatCount()}</span>
      <button onClick={handleIncrement} className="btn btn-secondary btn-sm">
        Increase
      </button>
      <button
        onClick={() => props.onDelete(props.counter.id)}
        className="btn btn-danger btn-sm m-2"
      >
        DELETE
      </button>
    </div>
  );

  function getBadgeClasses() {
    let classes = "badge m-2 badge-";
    classes += value === 0 ? "warning" : "primary";
    return classes;
  }

  function formatCount() {
    return value === 0 ? "zero" : value;
  }
}

export default Counter;
