import React, { useState } from "react";
import Counter from "./counter";

function Counters() {
  let [counters] = useState([
    { id: 1, value: 4 },
    { id: 2, value: 0 },
    { id: 3, value: 0 },
    { id: 4, value: 0 },
  ]);

  return (
    <div>
      {counters.map((counter) => (
        <Counter key={counter.id} value={counter.value} selected={true} />
      ))}
    </div>
  );
}

export default Counters;
